var mongoose = require('mongoose');
var db = mongoose.connection;
var Schema = mongoose.Schema;
db.on('error', console.error);
db.once('open', function() {
  // Create your schemas and models here.
  var userSchema = mongoose.Schema({
  name : String,
  mobno: String,
  reg_id: String
});

exports.users = mongoose.model('users', userSchema);

var messageSchema = mongoose.Schema({
	identifier_message_number : String,
	message : String,
	from : String,
	flag: String
	
})

exports.messagesTable = mongoose.model('messagesTable',messageSchema);


var totalSchema = mongoose.Schema({
	identifier : String,
	total_messages : String
});

exports.totalMessageTable = mongoose.model('totalMessageTable',totalSchema);
});



mongoose.connect('mongodb://localhost:27017/node-android-chat');

